// Calculate feature counts
const getFeatureCount = (competitorId: string): number => {
  let count = 0;
  featureCategories.forEach(cat => {
    cat.features.forEach(f => {
      const val = f[competitorId as keyof typeof f];
      if (val === true || val === 'coming') count++;
    });
  });
  return count;
};

